script:
const method = "sales_performance_dashboard.api.department_dashboard_api.get_department_discount_leakage_dashboard";
const storageDepartment = 'spd_department_dashboard_department';
const storageView = 'spd_department_view_mode';
const storageDate = 'spd_department_reference_date';

const trendRoot = root_element.querySelector('.ddl-trend');
const repsRoot = root_element.querySelector('.ddl-reps');
const customersRoot = root_element.querySelector('.ddl-customers');
const itemGroupsRoot = root_element.querySelector('.ddl-item-groups');
const tableBody = root_element.querySelector('.ddl-table-body');
const totalLeakageCell = root_element.querySelector('.ddl-total-value');

let trendChart = null;
let repsChart = null;
let customersChart = null;
let itemGroupsChart = null;

function asCurrency(value) {
  return format_currency(flt(value || 0));
}

function asPercent(value) {
  return `${flt(value || 0, 2).toFixed(2)}%`;
}

function setKpis(kpis) {
  const map = {
    leakage_amount: asCurrency(kpis.leakage_amount),
    leakage_pct: asPercent(kpis.leakage_pct),
    net_realization_pct: asPercent(kpis.net_realization_pct),
    avg_discount_pct: asPercent(kpis.avg_discount_pct),
  };

  root_element.querySelectorAll('[data-kpi]').forEach((el) => {
    const key = el.getAttribute('data-kpi');
    el.textContent = map[key] || '-';
  });
}

function renderTrend(trend) {
  const data = {
    labels: trend.labels || [],
    datasets: [
      { name: 'Leakage Amount', values: trend.amount || [] },
      { name: 'Leakage %', values: trend.pct || [] },
    ],
  };

  if (!trendChart) {
    trendChart = new frappe.Chart(trendRoot, {
      data,
      type: 'line',
      height: 250,
      colors: ['#dc2626', '#f59e0b'],
      axisOptions: { xIsSeries: 1, xAxisMode: 'tick' },
      lineOptions: { regionFill: 0 },
      tooltipOptions: {
        formatTooltipY: (value, y, i) => i === 0 ? asCurrency(value) : asPercent(value),
      },
    });
    return;
  }
  trendChart.update(data);
}

function renderTopReps(rows) {
  const data = {
    labels: (rows || []).map((d) => d.name),
    datasets: [{ name: 'Leakage Amount', values: (rows || []).map((d) => flt(d.leakage || 0)) }],
  };

  if (!repsChart) {
    repsChart = new frappe.Chart(repsRoot, {
      data,
      type: 'bar',
      height: 260,
      colors: ['#ef4444'],
      axisOptions: { xAxisMode: 'tick', xIsSeries: 1 },
      tooltipOptions: {
        formatTooltipY: (value) => asCurrency(value),
      },
    });
    return;
  }
  repsChart.update(data);
}

function renderTopCustomers(rows) {
  const data = {
    labels: (rows || []).map((d) => d.name),
    datasets: [{ values: (rows || []).map((d) => flt(d.leakage || 0)) }],
  };

  if (!customersChart) {
    customersChart = new frappe.Chart(customersRoot, {
      data,
      type: 'percentage',
      height: 260,
      colors: ['#8b5cf6', '#ec4899', '#06b6d4', '#22c55e', '#f97316', '#6366f1'],
    });
    return;
  }
  customersChart.update(data);
}

function renderItemGroups(rows) {
  const data = {
    labels: (rows || []).map((d) => d.name),
    datasets: [{ name: 'Leakage Amount', values: (rows || []).map((d) => flt(d.leakage || 0)) }],
  };

  if (!itemGroupsChart) {
    itemGroupsChart = new frappe.Chart(itemGroupsRoot, {
      data,
      type: 'bar',
      height: 260,
      colors: ['#7c3aed'],
      axisOptions: { xAxisMode: 'tick', xIsSeries: 1 },
      tooltipOptions: {
        formatTooltipY: (value) => asCurrency(value),
      },
    });
    return;
  }
  itemGroupsChart.update(data);
}

function renderTable(rows, total) {
  const html = (rows || []).map((d) => `
    <tr>
      <td>${d.idx}</td>
      <td>${frappe.datetime.str_to_user(d.date)}</td>
      <td>${frappe.utils.escape_html(d.invoice || '')}</td>
      <td>${frappe.utils.escape_html(d.customer || '')}</td>
      <td>${frappe.utils.escape_html(d.rep || '')}</td>
      <td class="right">${asCurrency(d.list_value)}</td>
      <td class="right">${asCurrency(d.billed_value)}</td>
      <td class="right ddl-red-t">${asCurrency(d.leakage)}</td>
      <td class="right">${asPercent(d.leakage_pct)}</td>
    </tr>
  `).join('');

  tableBody.innerHTML = html || '<tr><td colspan="9" class="ddl-empty">No discount leakage data in this period</td></tr>';
  totalLeakageCell.textContent = asCurrency(total || 0);
}

function load() {
  const department = localStorage.getItem(storageDepartment) || '';
  const view_mode = localStorage.getItem(storageView) || 'Monthly';
  const reference_date = localStorage.getItem(storageDate) || frappe.datetime.get_today();

  frappe.call({
    method,
    args: {
      department,
      view_mode,
      reference_date,
      limit: 8,
      table_limit: 20,
    },
  }).then((r) => {
    const payload = r.message || {};
    setKpis(payload.kpis || {});
    renderTrend(payload.trend || {});
    renderTopReps(payload.top_reps || []);
    renderTopCustomers(payload.top_customers || []);
    renderItemGroups(payload.item_groups || []);
    renderTable(payload.table || [], payload.total_leakage || 0);
  });
}

window.addEventListener('spd-department-changed', load);
load();
