script:
const kpiMethod = "sales_performance_dashboard.api.department_dashboard_api.get_department_kpis";
const ownerUsersMethod = "sales_performance_dashboard.api.department_dashboard_api.get_department_owner_users";
const storageKey = 'spd_department_dashboard_department';
const riskStorageKey = 'spd_department_risk_window';
const storageDate = 'spd_department_reference_date';

let ownerUsersCache = { department: '', users: [] };

function render(data) {
  root_element.querySelectorAll('[data-key]').forEach((el) => {
    const key = el.getAttribute('data-key');
    el.textContent = `${cint(data[key] || 0)}`;
  });
}

async function getOwnerUsers(department) {
  if (!department) return [];
  if (ownerUsersCache.department === department && ownerUsersCache.users.length) return ownerUsersCache.users;
  const r = await frappe.call({ method: ownerUsersMethod, args: { department } });
  const users = Array.isArray(r.message) ? r.message : [];
  ownerUsersCache = { department, users };
  return users;
}

function openList(doctype, filters) {
  frappe.route_options = filters || {};
  frappe.set_route('List', doctype);
}

async function openSourceByKey(key) {
  const department = localStorage.getItem(storageKey) || '';
  const ownerUsers = await getOwnerUsers(department);
  const ownerFilter = ownerUsers.length ? ['in', ownerUsers] : null;
  const filters = { docstatus: 1 };
  if (ownerFilter) filters.owner = ownerFilter;
  return openList('Sales Invoice', filters);
}

function bindCardClicks() {
  root_element.querySelectorAll('.dkc-card').forEach((card) => {
    const keyEl = card.querySelector('[data-key]');
    if (!keyEl) return;
    const key = keyEl.getAttribute('data-key');
    card.classList.add('dkc-clickable');
    card.title = 'Open source records';
    card.addEventListener('click', () => openSourceByKey(key));
  });
}

function loadKpis() {
  const department = localStorage.getItem(storageKey) || '';
  const risk_window_days = cint(localStorage.getItem(riskStorageKey) || 14);
  const reference_date = localStorage.getItem(storageDate) || frappe.datetime.get_today();
  frappe.call({ method: kpiMethod, args: { department, risk_window_days, reference_date } }).then((r) => render(r.message || {}));
}

bindCardClicks();
window.addEventListener('spd-department-changed', loadKpis);
loadKpis();
