script:
const kpiMethod = "sales_performance_dashboard.api.department_dashboard_api.get_department_kpis";
const storageKey = 'spd_department_dashboard_department';
const riskStorageKey = 'spd_department_risk_window';
const storageDate = 'spd_department_reference_date';

const currencyFields = new Set(['avg_deal_value', 'avg_won_deal_value']);
const dayFields = new Set(['avg_time_to_close_deal', 'avg_time_lead_to_deal']);

function fmt(key, value) {
  const v = value || 0;
  if (currencyFields.has(key)) return format_currency(v);
  if (dayFields.has(key)) return `${cint(v)} days`;
  return `${cint(v)}`;
}

function render(data) {
  root_element.querySelectorAll('[data-key]').forEach((el) => {
    const key = el.getAttribute('data-key');
    el.textContent = fmt(key, data[key]);
  });
}

function loadKpis() {
  const department = localStorage.getItem(storageKey) || '';
  const risk_window_days = cint(localStorage.getItem(riskStorageKey) || 14);
  const reference_date = localStorage.getItem(storageDate) || frappe.datetime.get_today();
  frappe.call({ method: kpiMethod, args: { department, risk_window_days, reference_date } }).then((r) => render(r.message || {}));
}

window.addEventListener('spd-department-changed', loadKpis);
loadKpis();
