script:
const method = "sales_performance_dashboard.api.personal_dashboard_api.get_my_sales_target_route";
const link = root_element.querySelector('.psd-target-shortcut-link');

link.addEventListener('click', (e) => {
	e.preventDefault();
	frappe.call({ method }).then((r) => {
		const data = r.message || {};
		if (data.has_target && data.target_name) {
			frappe.set_route('Form', 'Sales Targets', data.target_name);
			return;
		}
		const defaults = { target_level: 'Individual' };
		if (data.employee) {
			defaults.employee = data.employee;
		}
		frappe.new_doc('Sales Targets', defaults);
	});
});
