script:
const method = "sales_performance_dashboard.sales_performance_dashboard.dashboard_chart_source.personal_item_sales_monthly.personal_item_sales_monthly.get_table_data_for_custom";
const body = root_element.querySelector('.psd-item-table-body');
const totalEl = root_element.querySelector('.psd-item-table-total');

function money(value) {
	return format_currency(value || 0);
}

frappe.call({ method }).then((r) => {
	const payload = r.message || {};
	const rows = payload.rows || [];
	const total = payload.total || 0;

	if (!rows.length) {
		body.innerHTML = '<tr><td colspan="3" class="psd-item-table-empty">No data for this month</td></tr>';
		totalEl.textContent = money(0);
		return;
	}

	body.innerHTML = rows
		.map((row) => `<tr><td>${row.rank}</td><td>${frappe.utils.escape_html(row.item_name || '')}</td><td style=\"text-align:right\">${money(row.amount)}</td></tr>`)
		.join('');
	totalEl.textContent = money(total);
});
