script:
const method = "sales_performance_dashboard.sales_performance_dashboard.dashboard_chart_source.personal_top_customers.personal_top_customers.get_table_data_for_custom";
const PAGE_SIZE = 5;
const body = root_element.querySelector('.psd-top-customer-body');
const showMoreBtn = root_element.querySelector('.psd-top-customer-more');

let offset = 0;
let hasMore = false;
let loading = false;

function money(value) {
	return format_currency(value || 0);
}

function args(start = 0, page_length = PAGE_SIZE) {
	return {
		department: localStorage.getItem('spd_personal_dashboard_department') || '',
		employee: localStorage.getItem('spd_personal_dashboard_employee') || '',
		start,
		page_length,
	};
}

function openCustomer(customer) {
	if (!customer) return;
	frappe.set_route('Form', 'Customer', customer);
}

function rowHtml(row) {
	const customer = frappe.utils.escape_html(row.customer || '');
	return `<tr class=\"psd-top-customer-row\" data-customer=\"${customer}\" title=\"Open customer details\"><td>${row.rank}</td><td>${customer}</td><td style=\"text-align:right\">${money(row.amount)}</td></tr>`;
}

function renderNoData(message) {
	body.innerHTML = `<tr><td colspan=\"3\" class=\"psd-top-customer-empty\">${message}</td></tr>`;
}

function syncShowMore() {
	showMoreBtn.style.display = hasMore ? 'inline-flex' : 'none';
	showMoreBtn.disabled = loading;
	showMoreBtn.textContent = loading ? 'Loading...' : 'Show more';
}

function loadPage(append = false) {
	if (loading) return;
	loading = true;
	syncShowMore();

	frappe.call({ method, args: args(offset, PAGE_SIZE) })
		.then((r) => {
			const payload = r.message || {};
			const rows = payload.rows || [];
			hasMore = !!payload.has_more;

			if (!append) {
				if (!rows.length) {
					offset = 0;
					renderNoData('No data available');
					return;
				}
				body.innerHTML = rows.map((row) => rowHtml(row)).join('');
			} else if (rows.length) {
				body.insertAdjacentHTML('beforeend', rows.map((row) => rowHtml(row)).join(''));
			}

			offset += rows.length;
		})
		.catch(() => {
			if (!append) {
				offset = 0;
				hasMore = false;
				renderNoData('Could not load data');
			}
		})
		.finally(() => {
			loading = false;
			syncShowMore();
		});
}

function reload() {
	offset = 0;
	hasMore = false;
	loading = false;
	body.innerHTML = '<tr><td colspan=\"3\" class=\"psd-top-customer-empty\">Loading...</td></tr>';
	syncShowMore();
	loadPage(false);
}

body.addEventListener('click', (e) => {
	const row = e.target.closest('tr.psd-top-customer-row[data-customer]');
	if (!row) return;
	openCustomer(row.getAttribute('data-customer'));
});

showMoreBtn.addEventListener('click', () => loadPage(true));
window.addEventListener('spd-personal-changed', reload);
reload();
